mapper.select:
SELECT ID
FROM FAVORITE_CARCENTER
WHERE member_id =#{memberId}
AND carcenter_id=#{carcenterId}

SELECT fc.ID, fc.CARCENTER_ID, m.ADDRESS, m.PHONE_NUMBER, m.name as CARCENTER_NAME
FROM MEMBER m inner join FAVORITE_CARCENTER fc on m.id = fc.CARCENTER_ID
where fc.MEMBER_ID = #{memberId}
